<script>
    $(document).ready(function(){
        var $heroSlider = $(".hero__slider");

        $heroSlider.owlCarousel({
            margin: 20,
            items: 1,
            dots: true,
            dotsContainer: ".pagination-wrapper .owl-dots",
            animateOut: 'fadeOut',
            animateIn: 'fadeIn',
            smartSpeed: 1200,
            autoHeight: false,
            autoplay: false,
            onInitialized: updateCustomPagination,
            onChanged: updateCustomPagination
        });

        function updateCustomPagination(event) {
            var totalItems = event.item.count;

            // Hide dots and pagination if only one item
            if (totalItems <= 1) {
                $(".pagination-wrapper").addClass("d-none");
                return;
            } else {
                $(".pagination-wrapper").removeClass("d-none");
            }

            var currentItem = event.item.index - event.relatedTarget._clones.length / 2;
            if (currentItem < 0) {
                currentItem = totalItems + currentItem;
            }

            $(".custom-pagination .current").text(pad(currentItem + 1));
            $(".custom-pagination .total").text(pad(totalItems));
        }

        function pad(n) {
            return n < 10 ? '0' + n : n;
        }

        $('.our-partner-owl').owlCarousel({
            loop:false,
            margin:20,
            dots:true,
            responsiveClass:true,
            responsive:{
                0:{
                    items:2,
                },
                600:{
                    items:3,
                },
                1000:{
                    items:4,
                    margin:50,
                }
            }
        })

        $('.owl-dot').each(function(index) {
            $(this).attr('aria-label', `Go to slide ${index + 1}`);
        });

        $('.flash-mega-sale-owl').owlCarousel({
            loop:false,
            margin:16,
            dots:true,
            responsiveClass:true,
            responsive:{
                0:{
                    items:1,
                },
                600:{
                    items:2,
                },
                768:{
                    items:3,
                },
                1000:{
                    items:4,
                },
                1400:{
                    items:5,
                }
            }
        })

    });

    $(document).on('click', '.brand-col', function() {
        let brandName = $(this).data('brand');
        $(document).find('.product-rows').hide();
        
        if ($(brandName).length > 0) {
            $(document).find(brandName).show();
        } else {
            // $(document).find('.product-rows').show();
        }
        
        $('.brand-col img').css({ 'border': 'none' });

        $(this).find('img').css({
                    'border': '2px solid #292929',
                    'border-radius': '10px'
                }); 
    });

</script>
